import React, { useEffect } from "react";
import "./App.css";
import data from "./components/data";
import CardGrid from "./components/CharacterCards";
import uniqid from "uniqid";

function App() {
  const [characters, setCharacters] = React.useState(data.data);
  const [isLoading, setIsLoading] = React.useState(true);
  const [error, setError] = React.useState("");

  const randomNumberArray = [...Array(10)]
    .fill()
    .map((x) => Math.ceil(Math.random() * 75) + 1);

  useEffect(() => {
    handleAPI();
    setIsLoading(false);
  }, []);

  const handleAPI = () => {
    fetch("https://api.jikan.moe/v4/anime/38000/characters")
      .then((response) => response.json())
      .then((data) => setCharacters(data.data))
      .catch((err) => setError(err));
  };

  return (
    <div>
      {isLoading ? (
        <h1>loading... </h1>
      ) : (
        <CardGrid
          loading={isLoading}
          characters={randomNumberArray.map((num) => {
            return {
              name: characters[num].character.name,
              image: characters[num].character.images.jpg.image_url,
              clicked: false,
              id: uniqid(),
            };
          })}
        />
      )}
    </div>
  );
}

export default App;
