import React, { useEffect, useState } from "react";
import "./App.css";
import CharacterCards from "./components/CharacterCards";
import axios from "axios";
import uniqid from "uniqid";
import data from "./components/data";

function App() {
  const [apiData, setApiData] = useState(data.data);
  const [characters, setCharacters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);

  useEffect(() => {
    createCharacterList();
  }, [score]);

  const createCharacterList = () => {
    const characters = [];
    const randomNumbers = [...Array(10)].map(
      (x) => Math.ceil(Math.random() * 76) + 1
    );
    randomNumbers.map((num) => {
      return characters.push({
        name: apiData[num].character.name,
        image: apiData[num].character.images.jpg.image_url,
        clicked: false,
        id: uniqid(),
      });
    });
    setCharacters(characters);
    setIsLoading(false);
  };

  function handleClick(e) {
    console.log("clicked");
    if (e.target.clicked === false) {
      e.target.clicked = true;
      setScore((prevScore) => prevScore + 1);
    } else {
      setHighScore(score);
      setScore(0);
    }
  }

  return (
    <div>
      {console.log(isLoading, characters)}
      {isLoading ? (
        <h1>Loading</h1>
      ) : (
        <CharacterCards characters={characters} onClick={handleClick} />
      )}
    </div>
  );
}

export default App;
