import React from "react";

export default function CardGrid({ loading, characters }) {
  return loading ? (
    <h1>Loading...</h1>
  ) : (
    <div className="demo-grid">
      {characters.map((char) => {
        return (
          <div key={char.id}>
            <h4>{char.name}</h4>
            <img src={char.image} alt="images"></img>
          </div>
        );
      })}
    </div>
  );
}
