import React, { useEffect } from "react";
import "./App.css";
import CharacterCards from "./components/CharacterCards";
import axios from "axios";
import uniqid from "uniqid";
import data from "./components/data";

function App() {
  const [apiData, setApiData] = React.useState([]);
  const [characters, setCharacters] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [error, setError] = React.useState("");

  useEffect(() => {
    setApiData(data.data);
    createCharacterList();
  }, []);

  const createCharacterList = () => {
    const characters = [];
    const randomNumbers = [...Array(10)].map(
      (x) => Math.ceil(Math.random() * 76) + 1
    );
    randomNumbers.map((num) => {
      return characters.push({
        name: apiData[num].character.name,
        image: apiData[num].character.images.jpg.image_url,
        id: uniqid(),
      });
    });
    setCharacters(characters);
  };

  return <div>{console.log(characters)}</div>;
}

export default App;
