import React, { useEffect } from "react";
import "./App.css";
import CardGrid from "./components/CharacterCards";
import uniqid from "uniqid";

function App() {
  const [apiData, setApiData] = React.useState("");
  const [characters, setCharacters] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [error, setError] = React.useState("");

  const randomNumberArray = [...Array(10)]
    .fill()
    .map((x) => Math.ceil(Math.random() * 75) + 1);

  useEffect(() => {
    handleAPI();
  }, []);

  const handleAPI = () => {
    fetch("https://api.jikan.moe/v4/anime/38000/characters")
      .then((response) => response.json())
      .then((response) => setApiData(response.data))
      .then(
        setCharacters(
          randomNumberArray.map((num) => {
            return {
              name: apiData[num].character.name,
              image: apiData[num].character.images.jpg.image_url,
              clicked: false,
              id: uniqid(),
            };
          })
        )
      )
      .then(console.log(characters))
      .catch((err) => setError(err));

    setIsLoading(false);
  };

  return (
    <div>
      {/* {isLoading ? (
        <h1>loading... </h1>
      ) : (
        <CardGrid
          loading={isLoading}
          characters={randomNumberArray.map((num) => {
            return {
              name: characters[num].character.name,
              image: characters[num].character.images.jpg.image_url,
              clicked: false,
              id: uniqid(),
            };
          })}
        />
      )} */}
    </div>
  );
}

export default App;
