import React, { useEffect, useState } from "react";
import "./App.css";
import CharacterCard from "./components/CharacterCard";
import uniqid from "uniqid";
import data from "./components/data";

function App() {
  const [apiData, setApiData] = useState(data.data);
  const [characters, setCharacters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);
  const [clicked, setClicked] = useState([]);
  const [displayedCharacters, setDisplayedCharacters] = useState([]);

  useEffect(() => {
    createCharacterList();
  }, []);

  useEffect(() => {
    setDisplayedCharacters([]);
    if (characters.length > 0) {
      const newRandomIndexes = randomNumbers();
      newRandomIndexes.map((num) => {
        return setDisplayedCharacters((prevDisplayedCharacters) => [
          characters[num],
          ...prevDisplayedCharacters,
        ]);
      });
    }
    setIsLoading(false);
  }, [score]);

  const randomNumbers = () => {
    let array = [];
    for (let i = 0; array.length < 10; i++) {
      let num = Math.ceil(Math.random() * 152) + 1;
      if (array.indexOf(num) === -1) {
        array.push(num);
      }
    }
    return array;
  };

  const createCharacterList = () => {
    apiData.map((character) => {
      const newChar = {
        name: character.character.name,
        image: character.character.images.jpg.image_url,
        clicked: false,
        id: uniqid(),
      };
      return setCharacters((prevCharacters) => [newChar, ...prevCharacters]);
    });
  };

  const handleClick = (e) => {
    const id = e.target.id;
    setClicked((prevClicked) => [id, ...prevClicked]);
    if (clicked.includes(id)) {
      setHighScore(score);
      setScore(0);
    } else {
      const index = characters.findIndex((character) => character.id === id);
      characters[index].clicked = true;
      setScore((prevScore) => prevScore + 1);
    }
  };

  const characterCards = displayedCharacters.map((char) => {
    return (
      <CharacterCard
        key={char.id}
        src={char.image}
        alt={char.name}
        onClick={handleClick}
        id={char.id}
      />
    );
  });

  return (
    <div>
      <h1>Score: {score}</h1>
      <h3>High Score: {highScore}</h3>
      <div className="demo-grid">
        {isLoading ? "Loading..." : characterCards}
      </div>
    </div>
  );
}

export default App;
