import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header";
import Scoreboard from "./components/Scoreboard";
import CharacterCard from "./components/CharacterCard";
import uniqid from "uniqid";
import data from "./components/data";

function App() {
  const [apiData, setApiData] = useState(data.data);
  const [characters, setCharacters] = useState([]);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);
  const [clicked, setClicked] = useState([]);
  const [displayedCharacters, setDisplayedCharacters] = useState([]);
  const [charactersMapped, setCharactersMapped] = useState();

  useEffect(() => {
    createCharacterList();
    setScore(0);
  }, []);

  useEffect(() => {
    mapCharacterCards();
  }, [score]);

  const createNewDisplayCharacters = () => {
    setDisplayedCharacters([]);
    const newRandomIndexes = randomNumbers();
    newRandomIndexes.map((num) => {
      setDisplayedCharacters((prevDisplayedCharacters) => [
        characters[num],
        ...prevDisplayedCharacters,
      ]);
    });
    return mapCharacterCards();
  };

  const randomNumbers = () => {
    let array = [];
    for (let i = 0; array.length < 10; i++) {
      let num = Math.ceil(Math.random() * 76) + 1;
      if (array.indexOf(num) === -1) {
        array.push(num);
      }
    }
    return array;
  };

  const createCharacterList = () => {
    apiData.map((character) => {
      const newChar = {
        name: character.character.name,
        image: character.character.images.jpg.image_url,
        id: uniqid(),
      };
      setCharacters((prevCharacters) => [newChar, ...prevCharacters]);
      return createNewDisplayCharacters();
    });
  };

  const handleClick = (e) => {
    const id = e.target.id;

    if (clicked.includes(id)) {
      setHighScore(score);
      setScore(0);
      setClicked([]);
      createNewDisplayCharacters();
      mapCharacterCards();
    } else {
      setClicked((prevClicked) => [id, ...prevClicked]);
      setScore((prevScore) => prevScore + 1);
      createNewDisplayCharacters();
      mapCharacterCards();
    }
  };

  const mapCharacterCards = () => {
    if (displayedCharacters.length > 0) {
      const mapped = displayedCharacters.map((char) => {
        return (
          <CharacterCard
            src={char.image}
            alt={char.name}
            onClick={handleClick}
            id={char.id}
            key={char.id}
          />
        );
      });
      setCharactersMapped(mapped);
    }
  };

  return (
    <div>
      {console.log(charactersMapped)}
      <Header />
      <Scoreboard score={score} highScore={highScore} />
      <div className="demo-grid">
        {displayedCharacters.length > 0 ? charactersMapped : "Loading..."}
      </div>
    </div>
  );
}

export default App;
