import React from "react";
import uniqid from "uniqid";

export default function CardGrid({ loading, characters }) {
  let characterSet;
  const randomNumberArray = [...Array(10)]
    .fill()
    .map((x) => Math.ceil(Math.random() * 75) + 1);

  if (characters.length > 0) {
    randomNumberArray.map((num) => {
      return {
        name: characters[num].character.name,
        image: characters[num].character.images.jpg.image_url,
        clicked: false,
        id: uniqid(),
      };
    });
  }

  return loading ? (
    <h1>Loading...</h1>
  ) : (
    <div className="demo-grid">
      {characters.map((char) => {
        return (
          <div key={char.id}>
            <h4>{char.name}</h4>
            <img src={char.image} alt="images"></img>
          </div>
        );
      })}
    </div>
  );
}
