import React, { useEffect } from "react";
import "./App.css";
import uniqid from "uniqid";

function App() {
  const [data, setData] = React.useState({ characters: [] });
  const [loading, setLoading] = React.useState(true);
  const randomNumberArray = Array(10)
    .fill()
    .map(() => Math.floor(Math.random() * 150) + 1);

  useEffect(() => {
    fetch("https://api.jikan.moe/v4/anime/38000/characters")
      .then((response) => response.json())
      .then((data) =>
        data.data.map((x) => {
          return setData((prevState) => ({
            characters: [
              ...prevState.characters,
              {
                name: x.character.name,
                img_url: x.character.images.jpg.image_url,
                id: uniqid(),
                clicked: false,
              },
            ],
          }));
        })
      )
      .then(setLoading(false))
      .catch((err) => console.error(err));
  }, []);

  // const mappedCharacters = () => {
  //   const characters = randomNumberArray.map((char) => {
  //     return (
  //       <div key={data.characters[char].id}>
  //         <h3>{data.characters[char].name}</h3>
  //         <img
  //           src={data.characters[char].img_url}
  //           height="200"
  //           alt="demon-slayer-char"
  //         ></img>
  //       </div>
  //     );
  //   });
  //   return characters;
  // };

  return (
    <div className="demo-grid">
      {console.log(loading)}
      {loading ? <h1>Please wait...</h1> : console.log(randomNumberArray)}
    </div>
  );
}

export default App;
